import { createApp } from 'vue'
import App from './App.vue'

createApp(App).mount('#app')


function Rechercher(){
  let recherche = document.createElement("input");
  recherche.type = "text";
  recherche.placeholder = "Rechercher";
  header.appendChild(recherche);

  textes = document.querySelectorAll(".small");
  noms = document.querySelectorAll(".nom");
  recherche.addEventListener("keydown", function (event) {
      if (event.key === "Enter") {
        motRecherche = recherche.value.toLowerCase();
        console.log(motRecherche);

        for(let i = 0; i < cartes.length;i++){
          if (noms[i].innerHTML.toLowerCase().includes(motRecherche)) {
            cartes[i].style.display = 'block';
          } 
          else if (textes[i].innerHTML.toLowerCase().includes(motRecherche)){
            cartes[i].style.display = 'block';
          }
          else {
            cartes[i].style.display = 'none';
          }
        }

      }
  });
}
